    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
        // TODO add your handling code here:
        
        try
        {        
        int rno;
        rno=Integer.parseInt(JOptionPane.showInputDialog("Enter Roll No for delete" ));
        StudentDAO sd=new StudentDAO();
        Student S=new Student();
        S=sd.searchStudent(rno);
        if(S==null)
            JOptionPane.showMessageDialog(this, "Record Not Found");
        else
        {
            txtNo.setText(Integer.toString(S.getRno()));
            txtName.setText(S.getName());
            txtPhy.setText(Integer.toString(S.getPhy()));
            txtChem.setText(Integer.toString(S.getChem()));
            txtMath.setText(Integer.toString(S.getMath()));
              int n;
        n=JOptionPane.showConfirmDialog(this, "Are you Sure want to delete","delete", JOptionPane.YES_NO_OPTION);
       if(n==0)
       { 
            sd.deleteStudent(rno);
            clear();
       }
        }    
        }
        catch(Exception e)
        {
            System.out.println(e);
        }    
    }//GEN-LAST:event_btnDeleteActionPerformed

