    private void btnSearch1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSearch1ActionPerformed
        // TODO add your handling code here:
         try
        {        
        int rno;
        flag=2;
        rno=Integer.parseInt(JOptionPane.showInputDialog("Enter Roll No for update" ));
        StudentDAO sd=new StudentDAO();
        Student S=new Student();
        S=sd.searchStudent(rno);
        if(S==null)
            JOptionPane.showMessageDialog(this, "Record Not Found");
        else
        {
            txtNo.setText(Integer.toString(S.getRno()));
            txtName.setText(S.getName());
            txtPhy.setText(Integer.toString(S.getPhy()));
            txtChem.setText(Integer.toString(S.getChem()));
            txtMath.setText(Integer.toString(S.getMath()));
            enable1();
            txtNo.setEditable(false);
        }    
        }
        catch(Exception e)
        {
            System.out.println(e);
        }    
    }//GEN-LAST:event_btnSearch1ActionPerformed
    
