void clear()
{
   txtNo.setText("");
   txtName.setText("");
   txtPhy.setText("");
   txtChem.setText("");
   txtMath.setText("");
}

void enable1()
{
   txtNo.setEditable(true);
   txtName.setEditable(true);
   txtPhy.setEditable(true);
   txtChem.setEditable(true);
   txtMath.setEditable(true);
}

void disable1()
{
   txtNo.setEditable(false);
   txtName.setEditable(false);
   txtPhy.setEditable(false);
   txtChem.setEditable(false);
   txtMath.setEditable(false);
}

